<script lang="ts" setup>
import router from "../router";
import {useRoute} from "vue-router";
import {onMounted, reactive, ref} from "vue";
const i=ref(null)
onMounted(()=>{
  const route=useRoute()
  const path = route.path;
  if(path==="/main/item")i.value="first"
  else if(path==="/main/purchase")i.value="second"
  else if(path==="/main/message")i.value="third"
  else if(path==="/main/home")i.value="fourth"
  console.log(path)
})
const goto=(id)=>{
  if(id===1){
    i.value="first"
    router.push({name:'item'})
  }
  if(id===2){
    i.value="second"
    router.push({name:'purchase'})
  }
  if(id===3){
    i.value="third"
    router.push({name:'message'})
  }
  if(id===4){
    i.value="fourth"
    router.push({name:'home'})
  }
}
</script>

